INSERT INTO `sp_products` VALUES ('81', ',7,', '气体质量流量传感器', 'FS1015', '气体,流量,传感器', '[{\"url\":\"\\/Uploads\\/20140919\\/541b99460a2e0.jpg\",\"alt\":\"FS1015\"}]', ',12,', '专为呼吸机和麻醉机的流量计量需求而设计  \r\n高灵敏度，应用热式质量流量计原理可测量最低至8mm/sec的流体流量  \r\n无旁通结构，低压损  \r\n传感器的零点稳定度高 \r\n经消毒液杀菌处理后可重复使用 \r\n自动转换量程的开关设计提高了测量精度  \r\n主机设备上的用户界面可轻松实现线性输出', '<p><strong><span style=\"COLOR: #333333; FONT-SIZE: 16px\">产品描述</span></strong><span style=\"COLOR: #333333; FONT-SIZE: 16px\">：</span></p><p><span style=\"COLOR: #333333; FONT-SIZE: 16px\">FS1015系列微机电气体质量流量传感器是专门为各类医学用气体的测量和过程控制而设计的。产品采用微机电系统（MEMS）流量传感芯片来制作，适用于各类清洁气体。成本低、易安装、不需要温度压力补偿，可替代容积式或压差式的传统流量计。</span></p><p>&nbsp;</p><p><strong><span style=\"COLOR: #333333; FONT-SIZE: 16px\">产品特点：</span></strong></p><ul class=\" list-paddingleft-2\" type=\"disc\"><li><p><span style=\"COLOR: #333333; FONT-SIZE: 16px\">专为呼吸机和麻醉机的流量计量需求而设计&nbsp;</span></p></li><li><p><span style=\"COLOR: #333333; FONT-SIZE: 16px\">高灵敏度，应用热式质量流量计原理可测量最低至8mm/sec的流体流量&nbsp;</span></p></li><li><p><span style=\"COLOR: #333333; FONT-SIZE: 16px\">无旁通结构，低压损&nbsp;</span></p></li><li><p><span style=\"COLOR: #333333; FONT-SIZE: 16px\">传感器的零点稳定度高</span></p></li><li><p><span style=\"COLOR: #333333; FONT-SIZE: 16px\">经消毒液杀菌处理后可重复使用</span></p></li><li><p><span style=\"COLOR: #333333; FONT-SIZE: 16px\">自动转换量程的开关设计提高了测量精度&nbsp;</span></p></li><li><p><span style=\"COLOR: #333333; FONT-SIZE: 16px\">主机设备上的用户界面可轻松实现线性输出</span></p></li></ul><p><span style=\"COLOR: #333333; FONT-SIZE: 16px\">&nbsp;</span></p><p><span style=\"COLOR: #333333; FONT-SIZE: 16px\">机械图：</span></p><p><img title=\"FS1015.png\" src=\"http://www.isweek.cn/Uploads/20140919/541ba62d748ee.png\"/></p><p><span style=\"COLOR: #333333; FONT-SIZE: 16px\">&nbsp;</span></p><p><strong><span style=\"COLOR: #333333; FONT-SIZE: 16px\">技术参数：</span></strong></p><table style=\"WIDTH: 411.75pt\" border=\"0\" cellspacing=\"1\" cellpadding=\"0\" width=\"549\"><tbody><tr style=\"HEIGHT: 17.25pt\"><td style=\"BORDER-BOTTOM-COLOR: #ece9d8;