INSERT INTO `sp_products` VALUES ('83', ',7,', '一次性呼吸传感器', 'FS6022', '一次性,呼吸,传感器', '[{\"url\":\"\\/Uploads\\/20140919\\/541b9b681421f.jpg\",\"alt\":\"FS6022\"}]', ',12,', '可适用于婴儿呼吸机和成人呼吸机。可拆卸式设计将无法感应的流量减少到最小', '<p style=\"TEXT-ALIGN: left; LINE-HEIGHT: 20px; MARGIN-TOP: auto; MARGIN-BOTTOM: auto\"><strong><span style=\"FONT-FAMILY: 幼圆; COLOR: #333333; FONT-SIZE: 16px\">产品描述：</span></strong></p><p style=\"TEXT-ALIGN: left; LINE-HEIGHT: 20px; MARGIN-TOP: auto; MARGIN-BOTTOM: auto\"><span style=\"FONT-FAMILY: 幼圆; COLOR: #333333; FONT-SIZE: 16px\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;FS6022系列一次性呼吸传感器是专为医院便携式呼吸机而设计的气体流量传感器，可适用于婴儿呼吸机和成人呼吸机。可拆卸式设计将无法感应的流量减少到最小，提高了精确度。MEMS技术将在保证了产品可靠性和性能的同时,带给您一个具有竞争力的价格。</span></p><p style=\"TEXT-ALIGN: left; LINE-HEIGHT: 20px; MARGIN-TOP: auto; MARGIN-BOTTOM: auto\"><strong><span style=\"FONT-FAMILY: 幼圆; COLOR: #333333; FONT-SIZE: 16px\">产品规格：</span></strong></p><p style=\"TEXT-ALIGN: left; LINE-HEIGHT: 20px; MARGIN: 0px 0px 0px 48px\"><span style=\"FONT-FAMILY: 幼圆; COLOR: #333333; FONT-SIZE: 16px\">流量范围: 0~250 SLPM<br/>流动阻力: &lt;0.1 cmH2O/L/sec<br/>响应时间 (95%): 8 msec <br/>精度: ± 5 % 可达到 (&lt; 10 %)<br/>流动方向: 双向<br/>输出方式线性; 模拟 0.0 ~ 5.0 Vdc<br/>供电方式: 5.0 Vdc<br/>工作温度: 0 ~ 60 ºC<br/>功耗: &lt; 20 mW<br/>流体阻力: 除腐蚀性极强的流体外，水和其他液体不会损害传感器</span></p><p style=\"TEXT-ALIGN: left; LINE-HEIGHT: 20px; MARGIN-TOP: auto; MARGIN-BOTTOM: auto\"><span style=\"FONT-FAMILY: 幼圆; COLOR: #333333; FONT-SIZE: 16px\">机械尺寸和电气接口：</span></p><p style=\"TEXT-ALIGN: left; LINE-HEIGHT: 20px; MARGIN-TOP: auto; MARGIN-BOTTOM: auto\"><img title=\"FS6022.png\" src=\"http://www.isweek.cn/Uploads/20140919/541ba4e4ccec4.png\"/></p><p style=\"TEXT-ALIGN: left; LINE-HEIGHT: 20px; MARGIN-TOP: auto; MARGIN-BOTTOM: auto\"><span style=\"FONT-FAMILY: 幼圆; COLOR: #333333; FONT-SIZE: 16px\">&nbsp;</span></p><p style=\"TEXT-ALIGN: center; LINE-HEIGHT: 20px; MARGIN-TOP: auto; MARGIN-BOTTOM: auto\"><br/></p><table style=\"PADDING-BOTTOM: 0px; PADDING-LEFT: 0px; WIDTH: 379px; PADDIN