INSERT INTO `sp_products` VALUES ('109', ',4,', '微熔式压力变送器', 'MSP5100系列', '微熔式,压力变送器', '[{\"url\":\"\\/Uploads\\/20140923\\/54211d5d03d2f.jpg\",\"alt\":\"MSP5100\"}]', ',14,', '1)重工业CE认证\r\n2)100V/m电磁干扰保护\r\n3)反向极性保护\r\n4)工作温度范围宽\r\n5)1%综合误差\r\n6)结构小巧\r\n7)-40℃～125℃工作温度范围\r\n\r\n', '<p><strong>产品描述：</strong><strong>&nbsp;&nbsp;&nbsp;</strong><strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </strong></p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; MSP5100型微熔式压力变送器典型应用于高级HVAC控制，制冷系统，自动检测系统，空压机，工业过程控制.泵和压缩机，液压/气动系统，农机设备，能源和水处理系统等方面。<br/><br/><strong>特性：</strong></p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 1)重工业CE认证<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 2)100V/m电磁干扰保护<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 3)反向极性保护<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;4)工作温度范围宽<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 5)1%综合误差<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;6)结构小巧<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 7)-40℃～125℃工作温度范围<br/><br/><strong><span style=\"FONT-SIZE: 16px\">性能参数<br/></span></strong><span style=\"FONT-SIZE: 16px\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 1) 压力范围<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;50,100,200,300,500,1000,3000,5000,10,000PSI <br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 2)线性精度<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ±025%FS(BFSL)&nbsp;<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;3)介质兼容性<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 与174PH不锈钢(316L不锈钢可选)兼容的各种介质<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 4)压力循环<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &gt;107次满压循环<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 5)过载压力<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 2倍额定压力<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 6)破坏压力<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&