INSERT INTO `sp_products` VALUES ('136', ',9,', '氧气传感器 ', 'KE-25/KE-50', '氧气传感器,氧电池', '[{\"url\":\"\\/Uploads\\/20140924\\/5422896ae8f58.jpg\",\"alt\":\"KE\\u7cfb\\u5217\"}]', ',11,', '（1）长寿命　 \r\n（2）不受CO2，CO，H2S，NOx，H2影响　 \r\n（3）低成本　 \r\n（4）在常温下工作　 　　 \r\n（5）信号输出稳定　 \r\n（6）无需外部电源　 \r\n（7）不需加热 \r\n', '<p><span style=\"FONT-SIZE: 16px\"><span style=\"FONT-SIZE: 18px\"><p><span style=\"FONT-SIZE: 16px\"><strong>特性</strong><br/>（1）长寿命　 <br/>（2）不受CO2，CO，H2S，NOx，H2影响　 <br/>（3）低成本　 <br/>（4）在常温下工作　 　　 <br/>（5）信号输出稳定　 <br/>（6）无需外部电源　 <br/>（7）不需加热 <br/>　　 <br/><strong>技术参数</strong><br/>（1）测量范围　 0-100％O2　　　　　　 <br/>（2）精度　 KE-25：±1％（全量程）；KE-50： ±2％（全量程） <br/>（3）工作温度　 5～40℃　　　　　　　　　 <br/>（4）储存温度　 -20～＋60℃　　　　　　　 <br/>（5）响应时间　 KE-25：14±2秒；KE-50：60±5秒 <br/>（6）</span></p><p></p></span>空气中输出 　KE-25：10.0–15.5mv；KE-50：47.0-65.0mv”<span style=\"FONT-SIZE: 16px\"><span style=\"FONT-SIZE: 16px\"></span></span></span></p><p><span style=\"FONT-SIZE: 16px\"><span style=\"FONT-SIZE: 16px\">（7）期望寿命　 KE-25：5年；KE-50：10年</span></span><span style=\"FONT-SIZE: 16px\"><span style=\"FONT-SIZE: 16px\"> </span></span><span style=\"FONT-SIZE: 16px\"><span style=\"FONT-SIZE: 16px\"><br/><span style=\"LINE-HEIGHT: 0px; DISPLAY: none\" id=\"_baidu_bookmark_start_34\">‍</span></span></span></p><p>&nbsp;</p><p><strong><span style=\"FONT-SIZE: 16px\"><span style=\"FONT-SIZE: 16px\">应用</span></span></strong><span style=\"FONT-SIZE: 16px\"><span style=\"FONT-SIZE: 16px\"><br/>（1）医疗：麻醉机，呼吸机 <br/>（2）生物： 氧气孵卵器 <br/>（3）食品行业：冷藏室, 温室 <br/>（4）安全行业：空调 <br/>（5）氧气探测器<br/></span></span></p><p></p><p>&nbsp;</p>', '氧气传感器（KE-25 KE-50），氧电池，长寿命，低成本，不受不受CO2，CO，H2S，NOx，H2影响。', '/Uploads/20140929/54291ff753e44.pdf', '13', '1411549550', '1', '13', '1411550062', '1411981308', '0', '0', '1', '0');
