INSERT INTO `sp_products` VALUES ('143', ',8,', 'PID光离子气体传感器(大量程)', 'PID-A1', 'PID光离子气体传感器,小量程', '[{\"url\":\"\\/Uploads\\/20140925\\/5423c99b48cad.jpg\",\"alt\":\"PID-A1\"}]', ',11,', '1、 A系列标准尺寸，带有微型聚光光源。 \r\n2、 测量范围：300ppm\r\n3、 工作环境，-40到55度，相对湿度0-95％ \r\n4、 工作电压，3-3.6V \r\n5、 功耗，110mW \r\n6、 过载：6000ppm（以异丁烯参考） \r\n8、 在自由扩散情况下响应时间小于3秒 \r\n9、 使用寿命大于5年（不含UV灯和栅极） \r\n10、 A系列标准尺寸插针 ', '<p><strong>产品描述：</strong> <br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 光电离子探测器（PHOTO IONIZATION DETECTORS）可以测量(100ppb-6000ppm)量程范围的VOC（可挥发性有机物）和一些有毒气体。许多有害物质原料都含有VOC，PID由于其对VOC的高灵敏度，成为有害物质早期危险报警、泄漏监测等不可缺少的实用工具。 <br/><br/><strong>特性：</strong> <br/>1、 A系列标准尺寸，带有微型聚光光源。 <br/>2、 测量范围：300ppm<br/>3、 工作环境，-40到55度，相对湿度0-95％ <br/>4、 工作电压，3-3.6V <br/>5、 功耗，110mW <br/>6、 过载：6000ppm（以异丁烯参考） <br/>8、 在自由扩散情况下响应时间小于3秒 <br/>9、 使用寿命大于5年（不含UV灯和栅极）</p><p>10、A系列标准尺寸插针</p><p><br/><br/>&nbsp;</p>', '光电离子探测器（PHOTO IONIZATION DETECTORS）可以测量(100ppb-6000ppm)量程范围的VOC（可挥发性有机物）和一些有毒气体。许多有害物质原料都含有VOC，PID由于其对VOC的高灵敏度，成为有害物质早期危险报警、泄漏监测等不可缺少的实用工具。 \r\n\r\n', '', '13', '1411631294', '1', '13', '1411631325', '1411631520', '0', '0', '1', '0');
