INSERT INTO `sp_products` VALUES ('153', ',8,', '氧气传感器', 'O2-A2', '氧气传感器,电化学', '[{\"url\":\"\\/Uploads\\/20140925\\/5423d6e30db64.jpg\",\"alt\":\"O2-A2\"}]', ',11,', '（1）氧气传感器测量范围(%): 0-30 \r\n（2）工作寿命: > 24 达到初始信号的85%时的月数 \r\n（3）尺寸（mm）: Φ20.3×16.8mm \r\n（4）输出: 80 - 120 μA @ 22°C, 20.9% O2 \r\n（5）响应时间t90(秒): < 15 从20.9%到0(负载47Ω) \r\n（6）线性度(ppm): <0.6 全量程时线性误差(零点,400ppm) \r\n（7）重量: < 16g \r\n（8）温度范围: -30 - 55℃    压力范围:80 - 120Kpa    湿度范围: 5 - 95%RH \r\n（9）储存时间: 6月(储存温度3 - 20℃) \r\n（10）负载电阻:47 - 100欧姆 \r\n\r\n', '<p><strong>产品描述：</strong></p><p><strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</strong>氧气传感器（O2-A2）主要用于测量环境中氧气气体浓度，应用广泛，在煤矿，钢铁，石油化工，医疗等都大量使用，典型应用于氧气报警器，气氛分析仪&nbsp;。<br/></p><p><strong>特性：</strong> </p><p>（1）氧气传感器测量范围(%): 0-30 <br/>（2）工作寿命: &gt; 24 达到初始信号的85%时的月数 <br/>（3）尺寸（mm）: Φ20.3×16.8mm <br/>（4）输出: 80 - 120 μA @ 22°C, 20.9% O2 <br/>（5）响应时间t90(秒): &lt; 15 从20.9%到0(负载47Ω) <br/>（6）线性度(ppm): &lt;0.6 全量程时线性误差(零点,400ppm) <br/>（7）重量: &lt; 16g <br/>（8）温度范围: -30 - 55℃ <br/>（9）压力范围:80 - 120Kpa <br/>（10）湿度范围: 5 - 95%RH <br/>（11）储存时间: 6月(储存温度3 - 20℃) <br/>（12）负载电阻:47 - 100欧姆 <br/><br/><strong>&nbsp;</strong><br/></p>', ' 氧气传感器（O2-A2）主要用于测量环境中氧气气体浓度，应用广泛，在煤矿，钢铁，石油化工，医疗等都大量使用，典型应用于氧气报警器，气氛分析仪 。\r\n\r\n', '/Uploads/20141009/5436322468286.pdf', '13', '1411634917', '1', '13', '1411637153', '1412837927', '0', '0', '1', '0');
