INSERT INTO `sp_products` VALUES ('208', ',10,', '替代催化燃烧的红外气体传感器PrimePell', ' PrimePell', '替代催化燃烧的红外气体传感器,PrimePell', '[{\"url\":\"\\/Uploads\\/20140928\\/54277ffff135c.jpg\",\"alt\":\"PrimePell\"}]', ',11,', '模拟催化燃烧电桥输出\r\n 在气体里通过检测端的电压升高，和催化燃烧传感器一样。\r\n 对关键碳氢化合物的相对响应和催化燃烧相似\r\n工作电压范围3.0V-5.0V\r\n工作电流典型值为80mA\r\n最新的MEMS探测器技术\r\n量程：从0-100%Lel甲烷，0-100%Lel碳氢或0-100%Vol甲烷\r\n\r\n', '<span style=\"FONT-SIZE: 14px\"><span style=\"FONT-FAMILY: 宋体; FONT-SIZE: 14px\"><span style=\"FONT-FAMILY: 宋体; FONT-SIZE: 14px\"></span></span></span><p></p><p><strong>产品描述：</strong></p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 替代催化燃烧的红外气体传感器PrimePell：ARM7内核微处理器集成电路提供的输出电压与电源极性无关。传感器可以设置为线性电压输出，全量程0.4V-2.0V参考供电电源负极，或者设置为催化燃烧格式输出，通常零点是中间电压，相对于检测管脚在满量程点的电压是100mV。在催化燃烧配置时，PrimePell可以在满足电源供电要求的条件下，不改变电路并完全替代催化燃烧传感器。<br/><br/><strong>特性： </strong></p><p>l&nbsp;&nbsp;&nbsp;&nbsp;模拟催化燃烧电桥输出</p><p>l&nbsp;&nbsp;&nbsp;&nbsp;在气体里通过检测端的电压升高，和催化燃烧传感器一样。</p><p>l&nbsp;&nbsp;&nbsp; 对关键碳氢化合物的相对响应和催化燃烧相似</p><p>l&nbsp;&nbsp;&nbsp;&nbsp;工作电压范围3.0V-5.0V</p><p>l&nbsp;&nbsp;&nbsp;&nbsp;工作电流典型值为80mA</p><p>l&nbsp;&nbsp;&nbsp; 最新的MEMS探测器技术</p><p>l&nbsp;&nbsp;&nbsp;&nbsp;量程：从0-100%Lel甲烷，0-100%Lel碳氢或0-100%Vol甲烷</p><p></p><p></p>', '替代催化燃烧的红外气体传感器PrimePell：ARM7内核微处理器集成电路提供的输出电压与电源极性无关。传感器可以设置为线性电压输出，全量程0.4V-2.0V参考供电电源负极，或者设置为催化燃烧格式输出，通常零点是中间电压，相对于检测管脚在满量程点的电压是100mV。 ', '', '13', '1411874823', '1', '13', '1411874853', '0', '0', '0', '1', '0');
